mapper.update:
UPDATE tb_order
SET

    arrive_time = NOW(),


    arrive_time = NOW(),
    waiting_minute = TIMESTAMPDIFF(MINUTE,arrive_time,NOW()),


    end_time = NOW(),

`status` = #{status}
WHERE id = #{orderId}

    AND customer_id = #{customerId}


    AND driver_id = #{driverId}

mapper.delete:
DELETE FROM tb_order
WHERE id = #{orderId}

    AND driver_id = #{driverId}


    AND customer_id = #{customerId}

mapper.select:
SELECT CAST(id AS CHAR) AS id
FROM tb_order
WHERE uuid = #{uuid}

SELECT status
FROM tb_order
WHERE id = #{orderId}

    AND driver_id = #{driverId}


    AND customer_id = #{customerId}

SELECT CAST(id AS CHAR) AS id
FROM tb_order
WHERE customer_id = #{customerId}
AND `status` IN (2,3,4) LIMIT 1;

SELECT start_place AS startPlace,
       start_place_location AS startPlaceLocation,
       end_place AS endPlace,
       end_place_location AS endPlaceLocation,
       `status`
FROM tb_order
WHERE id = #{orderId}

    AND customer_id = #{customerId}


    AND driver_id = #{driverId}

LIMIT 1;
