mapper.update:
UPDATE tb_order
SET

    arrive_time = NOW(),


    arrive_time = NOW(),
    waiting_minute = TIMESTAMPDIFF(MINUTE,arrive_time,NOW()),


    end_time = NOW(),

`status` = #{status}
WHERE id = #{orderId}

    AND customer_id = #{customerId}


    AND driver_id = #{driverId}

UPDATE tb_order
SET real_mileage   = #{realMileage},
    return_mileage = #{returnMileage},
    incentive_fee  = #{incentiveFee},
    real_fee       = #{total}
WHERE id = #{orderId}

UPDATE tb_order
SET prepay_id = #{prepayId}
WHERE id = #{orderId}

UPDATE tb_order
SET pay_id   = #{payId},
    `status` = 7,
    pay_time = #{payTime}
WHERE uuid = #{uuid}

UPDATE tb_order
SET status   = 7,
    pay_id   = #{payId},
    pay_time = #{payTime}
WHERE id = #{orderId}

mapper.delete:
DELETE FROM tb_order
WHERE id = #{orderId}

    AND driver_id = #{driverId}


    AND customer_id = #{customerId}

mapper.select:
SELECT CAST(id AS CHAR) AS id
FROM tb_order
WHERE uuid = #{uuid}

SELECT status
FROM tb_order
WHERE id = #{orderId}

    AND driver_id = #{driverId}


    AND customer_id = #{customerId}

SELECT CAST(id AS CHAR) AS id
FROM tb_order
WHERE customer_id = #{customerId}
AND `status` IN (2,3,4) LIMIT 1;

SELECT start_place AS startPlace,
       start_place_location AS startPlaceLocation,
       end_place AS endPlace,
       end_place_location AS endPlaceLocation,
       `status`
FROM tb_order
WHERE id = #{orderId}

    AND customer_id = #{customerId}


    AND driver_id = #{driverId}

LIMIT 1;

SELECT COUNT(*)
FROM tb_order
WHERE 1 = 1

    AND id = #{orderId}


    AND customer_id = #{customerId}


    AND driver_id = #{driverId}


    AND date BETWEEN #{startDate} AND #{endDate}


    AND `status` = #{status}

SELECT CAST(id AS CHAR) AS id,
       CAST(start_place AS CHAR) AS startPlace,
       CAST(end_place AS CHAR) AS endPlace,
       CAST(real_mileage AS CHAR) AS realMileage,
       CAST(real_fee AS CHAR) AS realFee,
       `status`,
       DATE_FORMAT(create_time,'%Y-%m-%d %H:%i') AS createTime
FROM tb_order
WHERE 1=1

    AND id = #{orderId}


    AND customer_id = #{customerId}


    AND driver_id = #{driverId}


    AND date BETWEEN #{startDate} AND #{endDate}


    AND `status` = #{status}

ORDER BY id DESC
LIMIT #{start},#{length}

SELECT COUNT(*)
FROM tb_order
WHERE id = #{orderId}
  AND driver_id = #{driverId}

SELECT CAST(o.id AS CHAR) AS id, CAST(o.driver_id AS CHAR) AS driverId, CAST(o.customer_id AS CHAR) AS customerId, o.start_place AS startPlace, o.start_place_location AS startPlaceLocation, o.end_place AS endPlace, o.end_place_location AS endPlaceLocation, CAST(b.total AS CHAR) AS total, CAST(b.real_pay AS CHAR) AS realPay, CAST(b.mileage_fee AS CHAR) AS mileageFee, CAST(o.favour_fee AS CHAR) AS favourFee, CAST(o.incentive_fee AS CHAR) AS incentiveFee, CAST(b.waiting_fee AS CHAR) AS waitingFee, CAST(b.return_fee AS CHAR) AS returnFee, CAST(b.parking_fee AS CHAR) AS parkingFee, CAST(b.toll_fee AS CHAR) AS tollFee, CAST(b.other_fee AS CHAR) AS otherFee, CAST(b.voucher_fee AS CHAR) AS voucherFee, CAST(o.real_mileage AS CHAR) AS realMileage, o.waiting_minute AS waitingMinute, b.base_mileage AS baseMileage, CAST(b.base_mileage_price AS CHAR) AS baseMileagePrice, CAST(b.exceed_mileage_price AS CHAR) AS exceedMileagePrice, b.base_minute AS baseMinute, CAST(b.exceed_minute_price AS CHAR) AS exceedMinutePrice, b.base_return_mileage AS baseReturnMileage, CAST(b.exceed_return_price AS CHAR) AS exceedReturnPrice, CAST(o.return_mileage AS CHAR) AS returnMileage, o.car_plate AS carPlate, o.car_type AS carType, o.status, DATE_FORMAT(o.create_time, '%Y-%m-%d %H:%i:%s') AS createTime FROM tb_order o JOIN tb_order_bill b ON o.id = b.order_id WHERE o.id = #{orderId}
AND driver_id = #{driverId}
AND customer_id = #{customerId}

SELECT COUNT(*)
FROM tb_order
WHERE id = #{orderId}
  AND driver_id = #{driverId}
  AND customer_id = #{customerId}

SELECT CAST(o.id AS CHAR) AS id, DATE_FORMAT(o.accept_time, '%Y-%m-%d %H:%i:%s') AS acceptTime, DATE_FORMAT(o.accept_time, '%Y年%m月') AS `month`, CAST(o.real_fee AS CHAR) AS realFee, o.`status`, o.start_place AS startPlace, o.end_place AS endPlace, IFNULL(c.rate, -1) AS rate FROM tb_order o LEFT JOIN tb_order_comment c ON o.id = c.order_id WHERE 1=1
AND o.driver_id = #{driverId}
AND o.`status`=#{status}
ORDER BY o.id DESC LIMIT #{start}, #{length}

SELECT COUNT(*)
FROM tb_order
WHERE 1=1

    AND driver_id = #{driverId}


    AND `status`=#{status}
